import com.example.wechat.R;
import com.example.wechat.adapter.ChatListAdapter;
import com.example.wechat.model.ChatList;

import java.util.ArrayList;
import java.util.List;

public class ChatsFragment extends Fragment {


    public ChatsFragment() {
        // Required empty public constructor
    }

    private List<ChatList> list = new ArrayList<>();

    private RecyclerView recyclerView;


    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        getChatList();

    }

    private void getChatList() {

        list.add(new ChatList("1","Shuvo Talukdar","Hi","11/02/2022","data:image/jpeg;base64,"));

        list.add(new ChatList("2","Shimu Arika","Hello","03/12/2020","data:image/jpeg;base64,"));

        list.add(new ChatList("3","Rubel Miazie","what's up bro?","01/11/2020","data:image/jpeg;base64,"));

        list.add(new ChatList("4","Safayet Ahmed Khan Shafi","I am fine.","03/12/2020","https://akm-img-a-in.tosshub.com/sites/btmt/images/stories/home-loan_660_020117053409_030917101300_103018074036.jpg"));

        list.add(new ChatList("5","Asif","How are you?","12/12/2020","data:image/jpeg;base64,"));


    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_chats, container, false);

        recyclerView = view.findViewById(R.id.recyclerViewID);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

        recyclerView.setAdapter(new ChatListAdapter(list,getContext()));
        return view;
    }
}